import React, { Component } from "react";
import ReactDOM from "react-dom";

import "./index.css";
import logo from "./logo.svg";
import "./App.css";

import { createStore } from 'redux';

const reducer = (state = { count: 0 },action) => {

  console.log('reducer before state', oldState)

  if(action.type === '+1'){
    return {
      count: oldState.count + 1
    }
  }else if(action.type === '-1'){

  }

  return oldState
}

const store = createStore(reducer)
console.log(store.getState())

class App extends Component {

  renderDescription = () => {
    const count = store.getState().count;
    const remainder = count % 5;
    const upToNext = 5 - remainder;
    return `The current count is less than ${count + upToNext}`;
  };

  updateCount = (e) => {
    if(e.target.id === '-1'){
      store.dispatch({ type: '-1' })
      // this.setState({ count: store.getState().count - 1 })
    }else if(e.target.id === '+1'){
      store.dispatch({ type: '+1' })
      this.setState({ count: store.getState().count + 1 })
    }
  }

  render(){
    return (
      <div className="App">
        <Header />
        <Counter updateCount={this.updateCount} renderDescription={this.renderDescription} />
      </div>
    );
  }
}

const Header = (props) => {

  return (
    <header className="App-header">
      <img src={logo} className="App-logo" alt="logo" />
      <h1 className="App-title">Welcome to React</h1>
      <h3> {store.getState().count} </h3>
    </header>
  );
}

const Counter = (props) => {
  return (
    <div className="Counter">
      <h3> {props.renderDescription()} </h3>
      <button onClick={props.updateCount} id='-1'> - </button>
      <button onClick={props.updateCount} id='+1'> + </button>
    </div>
  );
}

ReactDOM.render(
  <App />,
  document.getElementById("root")
);
