import React, { Component } from "react";
import ReactDOM from "react-dom";

import "./index.css";
import logo from "./logo.svg";
import "./App.css";

import { createStore } from 'redux';


// 1. Store
// 2. Reducer
const reducer = (state = { count: 0 }, action) => {
  switch (action.type) {
    case 'INC':
      return { count: state.count + action.value }
      break;
    case 'MUL':
      return { count: state.count * action.value }
      break;
    default:
        return state
  }
}
const store = createStore(reducer, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())

class App extends Component {

  componentDidMount(){
    store.subscribe(() => this.forceUpdate())
  }

  renderDescription = () => {
    const count = store.getState().count;
    const remainder = count % 5;
    const upToNext = 5 - remainder;
    return `The current count is less than ${count + upToNext}`;
  };

  render(){
      return (
      <div className="App">
        <Header />
        <Counter renderDescription={this.renderDescription}/>
      </div>
    );
  }
}

class Header extends Component {
  render(){
    return (
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1 className="App-title">Welcome to React</h1>
        <h3> {store.getState().count} </h3>
        <h3>{this.props.renderDescription()}</h3>
      </header>
    );
  }
}

class Counter extends Component {

  render(){
    return (
      <div className="Counter">

        <button onClick={() => store.dispatch({ type: 'MUL', value: 3 })} id='3'> *3 </button>
        <button onClick={() => store.dispatch({ type: 'INC', value: -1 })} id='-1'> - </button>
        <button onClick={() => store.dispatch({ type: 'INC', value: +1 })} id='+1'> + </button>
        <button onClick={() => store.dispatch({ type: 'MUL', value: 5 })} id='*5'> *5 </button>

      </div>
    );
  }
}

ReactDOM.render(
  <App />,
  document.getElementById("root")
);
