import React, { Component } from "react";
import ReactDOM from "react-dom";

import "./index.css";
import logo from "./logo.svg";
import "./App.css";

import { createStore } from 'redux';


// 1. Store
// 2. Reducer
const reducer = (oldState={ count: 10 }, action) => {
  console.log(action)
  console.log(oldState)

  if(oldState === undefined){
    return {}
  }
  return oldState
}
const store = createStore(reducer)

class App extends Component {

  handleInput = (e) => {
    switch (e.target.id) {
      case '3':
        this.setState({ count: this.state.count * 3 })
        break;
      case '-1':
        this.setState({ count: this.state.count - 1 })
        break;
      case '+1':
        this.setState({ count: this.state.count + 1 })
        break;
      case '*5':
        this.setState({ count: this.state.count * 5 })
        break;
      default:

    }
  }

  render(){
    return (
      <div className="App">
        <Header count={this.state.count} />
        <Counter handleInput={this.handleInput} count={this.state.count} />
      </div>
    );
  }
}

const Header = (props) => {

  const renderDescription = () => {
    const count = props.count;
    const remainder = count % 5;
    const upToNext = 5 - remainder;
    return `The current count is less than ${count + upToNext}`;
  };

  return (
    <header className="App-header">
      <img src={logo} className="App-logo" alt="logo" />
      <h1 className="App-title">Welcome to React</h1>
      <h3> {props.count} </h3>
      <h3>{renderDescription()}</h3>
    </header>
  );
}

class Counter extends Component {

  render(){
    return (
      <div className="Counter">

        <button onClick={this.props.handleInput} id='3'> *3 </button>
        <button onClick={this.props.handleInput} id='-1'> - </button>
        <button onClick={this.props.handleInput} id='+1'> + </button>
        <button onClick={this.props.handleInput} id='*5'> *5 </button>

      </div>
    );
  }
}

ReactDOM.render(
  <App />,
  document.getElementById("root")
);
