import React, { Component } from "react";
import ReactDOM from "react-dom";

import "./index.css";
import logo from "./logo.svg";
import "./App.css";

import { createStore } from 'redux';


// 1. Store
// 2. Reducer
const reducer = (state = { count: 100 }, action) => {
  console.log(action)
  return state
}
const store = createStore(reducer)

class App extends Component {

  render(){
    return (
      <div className="App">
        <Header />
        <Counter />
      </div>
    );
  }
}

const Header = (props) => {

  const renderDescription = () => {
    const count = store.getState().count;
    const remainder = count % 5;
    const upToNext = 5 - remainder;
    return `The current count is less than ${count + upToNext}`;
  };

  return (
    <header className="App-header">
      <img src={logo} className="App-logo" alt="logo" />
      <h1 className="App-title">Welcome to React</h1>
      <h3> {store.getState().count} </h3>
      <h3>{renderDescription()}</h3>
    </header>
  );
}

class Counter extends Component {

  render(){
    return (
      <div className="Counter">

        <button onClick={null} id='3'> *3 </button>
        <button onClick={null} id='-1'> - </button>
        <button onClick={null} id='+1'> + </button>
        <button onClick={null} id='*5'> *5 </button>

      </div>
    );
  }
}

ReactDOM.render(
  <App />,
  document.getElementById("root")
);
